import React from 'react';
import '../App.css';
import { Button } from './Button';
import './HeroSection.css';

const HeroSection = () => {
  return (
    <div className='hero-container' id="sobre"> 
      <h1>João Deybson</h1>
      <p>Tenho 19 anos e estou cursando o ensino médio com curso técnico integrado de Informática no IFRN.</p>
      <div className='hero-btns'>
        <a 
          href='' // Substitua pelo caminho correto do seu PDF
          download
        >
          <Button 
            id="downloadBtn"
            className='btns'
            buttonStyle='btn--outline'
            buttonSize='btn--large'
          >
            Baixe meu portfólio
          </Button>
        </a>
      </div>
    </div>
  );
}

export default HeroSection;
