import React from 'react';
import '../App.css';
import { Button } from './Button';
import './HeroSection.css';

const HeroSection = () => {
  const downloadPortfolio = () => {
    const link = document.createElement('a');
    link.href = '/react_meusite/public/Pdf/Livro\ de\ Python\ \(desenvolvedores\).pdf'; // Caminho do PDF
    link.download = 'portfólio.pdf'; // Nome do arquivo ao baixar
    document.body.appendChild(link);
    link.click(); // Simula o clique no link
    document.body.removeChild(link); // Remove o link do DOM
  };


  return (
    <div className='hero-container' id="sobre"> 
      <h1>João Deybson</h1>
      <p>Tenho 19 anos e estou cursando o ensino médio com curso técnico integrado de Informática no IFRN.</p>
      <div className='hero-btns'>
        <Button 
          id="downloadBtn"
          className='btns'
          buttonStyle='btn--outline'
          buttonSize='btn--large'
          onClick={downloadPortfolio} // Adiciona o onClick para o botão
        >
          Baixe meu portfólio
        </Button>
      </div>
    </div>
  );
}

export default HeroSection;
